	CMP #(TILE_PROP_WATER |TILE_PROP_HARMFUL)
	BEQ Beached_NoDryCheep

	CMP #(TILE_PROP_FOREGROUND | TILE_PROP_WATER |TILE_PROP_HARMFUL)
	BNE Beached_NoDryCheep

	LDA #OBJ_FLAMINGCHEEP
