	LDA Sprite_RAM,Y
	SUB #$08
	STA Sprite_RAM+8,Y
	STA Sprite_RAM+12,Y

	LDA Sprite_RAM+3,Y
	STA Sprite_RAM+11,Y

	LDA Sprite_RAM+7,Y
	STA Sprite_RAM+15,Y

	LDA Sprite_RAM+2,Y
	AND #$BF
	STA Sprite_RAM+10,Y
	STA Sprite_RAM+14,Y

	LDA Objects_Frame, X
	ASL A
	TAX 
	LDA Flame_Frames, X
	STA Sprite_RAM+9,Y
	LDA Flame_Frames + 1, X
	STA Sprite_RAM+13,Y
	
	LDX <CurrentObjectIndexZ
	
	LDA Objects_XVelZ, X
	ORA Objects_YVelZ, X
	BEQ DryCheep_DrawFlamesAndSmoke1

	LDA BeachedCheep_PoofTimer, X
