	LDA Objects_Data5, X
	STA SpecialObj_XLo, Y
	LDA Objects_Data3, X
	STA SpecialObj_YLo, Y
	LDA <Objects_Data1, X
	STA SpecialObj_YHi, Y

	LDA #$0D
	STA BeachedCheep_PoofTimer, X

	LDA <Objects_XZ, X
	STA Objects_Data5, X
	LDA <Objects_YZ, X
	STA Objects_Data3, X
	LDA <Objects_YHiZ, X
