	LDA Objects_ID + 5
	CMP #OBJ_KEY
	BNE Phanto_Chase5

	LDA Objects_State + 5
	CMP #OBJSTATE_HELD
	BNE Phanto_Chase1

	LDA #$10
	STA Objects_Data3, X
	LDA <Counter_1
	AND #$01
	BNE Phanto_Chase0_1
	JSR Chase
	JMP Phanto_Chase0
	
