	EOR #$01
	STA Level_ChgTileValue
	INC Level_ChgTileEvent
	
	JSR SetObjectTileCoordAlignObj
	PLA
	PLA

PiranhaCheckOffScreen1:
	RTS

PiranahDeleteOffScreen:
	LDA #$01
	STA Objects_UseShortHTest, X
	JSR Object_DeleteOffScreen
	LDA #$00
	STA TempA
	RTS

PiranhaCheckTimer:
	LDA Objects_Timer, X
	BEQ PiranhaCheckTimer1

	JSR PiranhaCheckBeginning
	LDA Objects_Timer, X
	CMP #$01
	BNE PiranhaCheckTimer1

	LDA Objects_Data3, X
	CMP #TILE_PROP_ENEMY
	BEQ PiranhaCheckTimer0

	LDA #TILE_PROP_ENEMY
	STA Objects_Data3, X
	RTS

PiranhaCheckTimer0:
	LDA #(TILE_PROP_SOLID_ALL | TILE_PROP_HARMFUL)
	STA Objects_Data3, X

PiranhaCheckTimer1:
	RTS

DrawPiranhaGrow:
	LDA Objects_Data4, X
	AND #$08
	LSR A
	LSR A
	LSR A
	STA <Temp_Var1
	LDA Objects_Data5, X
	ASL A
	STA <Temp_Var2
	ORA <Temp_Var1
	STA Objects_Frame, X

	JSR Object_ShakeAndDrawAligned

	LDA Objects_Data5, X
	ASL A
	TAX
	LDA GrowAttributes, X
	ORA Sprite_RAM + 2, Y
	STA Sprite_RAM + 2, Y
	LDA GrowAttributes + 1, X
	ORA Sprite_RAM + 6, Y
	STA Sprite_RAM + 6, Y
	RTS

Piranha_CheckTiles:

	LDA Level_ChgTileEvent
	BEQ Piranha_CheckTiles2

Piranha_CheckTiles1:
	RTS

Piranha_CheckTiles2:

	LDA #$00
	STA <Temp_Var13
	JSR CheckBlockAbove
	CMP Objects_Data3, X
	BEQ Piranha_SetVelocity

	INC <Temp_Var13
	JSR CheckBlockRight
	CMP Objects_Data3, X
	BEQ Piranha_SetVelocity

	INC <Temp_Var13
	JSR CheckBlockBelow
	CMP Objects_Data3, X
	BEQ Piranha_SetVelocity
	
	INC <Temp_Var13
	JSR CheckBlockLeft
	CMP Objects_Data3, X
	BEQ Piranha_SetVelocity
	
	LDA #$00
	STA <Objects_XVelZ, X
	STA <Objects_YVelZ, X
	LDA #$80
	STA Objects_Timer, X

	JSR Object_GetAttrJustTile

	LDA Object_LevelTile
	EOR #$01
	STA Level_ChgTileValue
	INC Level_ChgTileEvent
	
	JSR SetObjectTileCoordAlignObj
	LDA #$00
	RTS

Piranha_SetVelocity:
	JSR Object_GetAttrJustTile

	LDA Object_LevelTile
	EOR #$01
	STA Level_ChgTileValue
	INC Level_ChgTileEvent
	
	JSR SetObjectTileCoordAlignObj
	LDY <Temp_Var13
	LDA PiranhaGrowXVel, Y
	STA <Objects_XVelZ, X
	LDA PiranhaGrowYVel, Y
	STA <Objects_YVelZ, X
	TYA
	STA Objects_Data5, X
	LDY Objects_Data3, X
	CPY #(TILE_PROP_SOLID_ALL | TILE_PROP_HARMFUL)
	BNE DrawPiranhaGrow1

	TAY
	LDA PiranhaFrameFlip, Y 
	STA Objects_Data5, X

DrawPiranhaGrow1:
	LDA #$00
	RTS