	LDA #$18

	BCS PRG002_AE282
	LDA #-$18

PRG002_AE282:
	STA Objects_XVel, X

	LDA #$D0
	STA Objects_YVel, X

	RTS		 ; Return


	; Sets X velocity by facing flip direction
Object_SetXVelByFacingDir:
	LDA Objects_FlipBits,X
	ASL A
	ASL A	; If HFlip is set, pushed into carry

	LDA #$08
