	JSR Player_StandOnPlatform	 ; Stand on platform
	LDA <Objects_XVel, X
	STA Player_CarryXVel
	LDA <Objects_YVel,X
	STA Player_CarryYVel

PRG002_BABC:
	LDA #$01
	STA Objects_Var5, X
	SEC		 ; Set carry (collided)
	RTS

PRG002_BABD:
	CLC
	RTS		 ; Return

