	JMP BadgeShop_RTS

Try_Buy_Badge:
	LDA <Pad_Input
	AND #PAD_A
	BEQ BadgeShop_RTS
	JSR Buy_Badge
BadgeShop_RTS:

	LDX DAIZ_TEMP1
	RTS

