	LDA Sound_QLevel1
	ORA #SND_LEVELBLIP
	STA Sound_QLevel1
	INC Item_Shop_Window
	INC Item_Shop_Window + 1
	INC Item_Shop_Window + 2
	LDX #$02

Check_OverFlow_Badge:
	LDA Item_Shop_Window, X
	CMP #Max_Item_Count
	BNE Next_Badge_Please
	LDA #$00
	STA Item_Shop_Window, X
Next_Badge_Please:
	DEX
