	LDA #$2A
	STA Graphics_Buffer, Y
	INY
	LDA #$AF
	STA Graphics_Buffer, Y
	INY
	LDA #$02
	STA Graphics_Buffer, Y
	INY
	LDA Badge_Prices, X
	JSR ToThreeDigits

Next_BadgePrice_Digit:
	LDA <Temp_Var2
	ORA #$30
	STA Graphics_Buffer, Y
	INY
	LDA <Temp_Var3
	ORA #$30
	STA Graphics_Buffer, Y
	INY
	LDA #$00
	STA Graphics_Buffer, Y
	LDA Graphics_BufCnt
	CLC
	ADC #$08
	STA Graphics_BufCnt
	RTS

Buy_Badge:
	JSR Clear_Calc
	LDX Item_Shop_Window + 1
	LDA Cherries
	SUB Badge_Prices, X
	BMI Cannot_Take_Badge
	BPL Take_Badge

Cannot_Take_Badge:
	LDA Sound_QMap		; Not enough coins
	ORA #SND_MAPDENY
	STA Sound_QMap
	RTS

Take_Badge:
	STA Cherries
	LDX Item_Shop_Window + 1
	INC Inventory_Items + 8, X
	LDA Sound_QLevel1
	ORA #SND_LEVELBLIP
	STA Sound_QLevel1
	RTS

BlockPlatformYVel:
	.byte $F0, $00, $00, $10

BlockPlatformXVel
	.byte $00, $10, $F0, $00

BlockPlatformMove:
	LDA #$00
	STA Objects_Var1, X
	LDA <Objects_X, X
	ORA <Objects_Y, X
	AND #$0F
	ORA Objects_XVelFrac, X
	ORA Objects_YVelFrac, X
	BEQ BlockPlatform0
	RTS

BlockPlatform0:
	LDA Objects_Var2, X
	AND #$01
	BNE BlockPlatform2

BlockPlatform1:
	LDA Objects_Var2, X
	INC Objects_Var2, X
	AND #$03
	JSR CheckNextBlock
	CMP #TILE_PROP_ENEMY
	BNE BlockPlatform1
	BEQ BlockPlatform3

BlockPlatform2:
	LDA Objects_Var2, X
	DEC Objects_Var2, X
	AND #$03
	JSR CheckNextBlock
	CMP #TILE_PROP_ENEMY
	BNE BlockPlatform2
	
BlockPlatform3:
	LDA <Temp_Var12
	TAY
	STA Objects_Var2, X
	LDA BlockPlatformYVel, Y
	STA <Objects_YVel, X
	LDA BlockPlatformXVel, Y
	STA <Objects_XVel, X
	RTS

CheckNextBlock:
	JSR DynJump

	.word CheckBlockPlatformAbove
	.word CheckBlockPlatformRight
	.word CheckBlockPlatformLeft
	.word CheckBlockPlatformBelow
	

SetBlockDirection:
	LDA <Temp_Var12
	TAY
	AND #$02
	STA Objects_Var2, X
	LDA BlockPlatformYVel, Y
	STA <Objects_YVel, X
	LDA BlockPlatformXVel, Y
	STA <Objects_XVel, X
	RTS

GetBlockPlatform:
	LDY #(OTDO_Water - Object_TileDetectOffsets)
	JSR Object_DetectTile
	RTS

CheckBlockPlatformAbove:
	LDA #$00
	STA <Temp_Var12
	LDA <Objects_Y, X
	STA <Temp_Var14
	SUB #$10
	STA <Objects_Y, X
	LDA <Objects_YHi, X
	STA <Temp_Var15
	SBC #$00
	STA <Objects_YHi, X
	JSR GetBlockPlatform
	PHA
	LDA <Temp_Var14
	STA <Objects_Y, X
	LDA <Temp_Var15
	STA <Objects_YHi, X
	PLA
	RTS

CheckBlockPlatformBelow:
	LDA #$03
	STA <Temp_Var12
	LDA <Objects_Y, X
	STA <Temp_Var14
	ADD #$10
	STA <Objects_Y, X
	LDA <Objects_YHi, X
	STA <Temp_Var15
	ADC #$00
	STA <Objects_YHi, X
	JSR GetBlockPlatform
	PHA
	LDA <Temp_Var14
	STA <Objects_Y, X
	LDA <Temp_Var15
	STA <Objects_YHi, X
	PLA
	RTS

CheckBlockPlatformRight:
	LDA #$01
	STA <Temp_Var12
	LDA <Objects_X, X
	STA <Temp_Var14
	ADD #$10
	STA <Objects_X, X
	LDA <Objects_XHi, X
	STA <Temp_Var15
	ADC #$00
	STA <Objects_XHi, X
	JSR GetBlockPlatform
	PHA
	LDA <Temp_Var14
	STA <Objects_X, X
	LDA <Temp_Var15
	STA <Objects_XHi, X
	PLA
	RTS

CheckBlockPlatformLeft:
	LDA #$02
	STA <Temp_Var12
	LDA <Objects_X, X
	STA <Temp_Var14
	SUB #$10
	STA <Objects_X, X
	LDA <Objects_XHi, X
	STA <Temp_Var15
	SBC #$00
	STA <Objects_XHi, X
	JSR GetBlockPlatform
	PHA
	LDA <Temp_Var14
	STA <Objects_X, X
	LDA <Temp_Var15
	STA <Objects_XHi, X
	PLA
	RTS

Diagonal_PodoboInitXVel:
	.byte $F0, $F0, $10, $10

Diagonal_PodoboInitYVel:
	.byte $10, $F0, $F0, $10

Diagonal_PodoboSpriteFlips:
	.byte SPR_VFLIP, $00, SPR_HFLIP, SPR_VFLIP | SPR_HFLIP

ObjInit_DiagonalPodobo:
	LDA Objects_Property, X
	TAY
	LDA Diagonal_PodoboInitXVel, Y
	STA Objects_XVel, X
	LDA Diagonal_PodoboInitYVel, Y
	STA Objects_YVel, X
	LDA Diagonal_PodoboSpriteFlips, Y
	STA Objects_FlipBits, X
	RTS

ObjNorm_DiagonalPodobo:
	LDA <Player_HaltGame
	BNE ObjNorm_DiagonalPodoboEnd

	JSR Object_DeleteOffScreen
	
	LDA Objects_Var1, X
	CMP #$0A
	BCS ObjNorm_DiagonalPodobo0

	INC Objects_Var1, X
	BNE ObjNorm_DiagonalPodobo01

ObjNorm_DiagonalPodobo0
	LDA #$00
	STA Objects_Var1, X
	
ObjNorm_DiagonalPodobo01:
	LDA Objects_Var1, X
	LSR A
	LSR A
	STA Objects_Frame, X

	JSR Object_ApplyXVel
	JSR Object_ApplyYVel_NoLimit
	LDA Objects_YVel, X
	PHA
	JSR Object_WorldDetect4
	PLA 
	STA Objects_YVel, X

	LDA <Objects_DetStat,X
	AND #(HIT_DET_GRND | HIT_DET_CEIL)
	BEQ ObjNorm_DiagonalPodobo1
	LDA <Objects_YVel,X	 
	JSR Negate
	STA <Objects_YVel,X
	LDA Objects_FlipBits, X
	EOR #SPR_VFLIP
	STA Objects_FlipBits, X

ObjNorm_DiagonalPodobo1:
	LDA <Objects_DetStat,X
	AND #(HIT_DET_LEFT | HIT_DET_RIGHT)
	BEQ ObjNorm_DiagonalPodobo2
	JSR Object_AboutFace

ObjNorm_DiagonalPodobo2:
	JSR Object_HitTestRespond

ObjNorm_DiagonalPodoboEnd:
	JMP Object_ShakeAndDraw

ObjInit_Birdo:
	LDA RandomN
	AND #$03
	TAY
	LDA Birdo_ShootTimers, Y
	STA Objects_Timer, X
	LDA #$04
	STA Objects_HitCount, X
	RTS

Birdo_Walk:
	.byte $00, $0C, $00, $F4

Birdo_EggShoot:
	.byte $20, $E0

Birdo_ShootTimers:
	.byte $80, $C0, $A0, $E0

ObjNorm_Birdo:
	LDA <Player_HaltGame
	BEQ ObjNorm_Birdo1
	JMP ObjNorm_BirdoDraw

ObjNorm_Birdo1:
	JSR Object_DeleteOffScreen
	JSR Object_FacePlayer
	
	LDA Objects_Var1, X
	BNE Birdo_PauseShoot

Birdo_Waltz:
	INC Objects_Var2, X
	LDA Objects_Var2, X
	AND #$C0
	CLC
	ROL A
	ROL A
	ROL A
	TAY
	LDA Birdo_Walk, Y
	STA Objects_XVel, X 

	LDA Objects_Timer, X
	BNE Birdo_Norm
	
	LDA #$20
	STA Objects_Timer, X

	LDA #$00
	STA Objects_XVel, X
	LDA #$02
	STA Objects_Var1, X
	BNE Birdo_Norm

Birdo_PauseShoot:
	LDA Objects_Timer, X
	BNE Birdo_TryShoot
	LDA RandomN
	AND #$03
	TAY
	LDA Birdo_ShootTimers, Y
	STA Objects_Timer, X
	LDA #$00
	STA Objects_Var1, X

Birdo_TryShoot:
	CMP #$08
	BNE Birdo_Norm

	LDA Objects_SprVVis,X
	AND #$80
	ORA Objects_SprHVis,X
	BNE Birdo_Norm

	LDA Birdo_EggShoot, Y
	STA <Temp_Var16

	JSR SpecialObj_FindEmpty
	CPY #$FF
	BEQ Birdo_Norm

	LDA #SOBJ_EGG
	STA SpecialObj_ID,Y

	LDA #$10
	STA SpecialObj_Timer,Y

	LDA <Objects_X,X
	STA SpecialObj_XLo,Y

	LDA <Objects_Y,X
	ADD #$02
	STA SpecialObj_YLo,Y

	LDA <Objects_YHi, X
	ADC #$00
	STA SpecialObj_YHi,Y

	LDA <Temp_Var16
	STA SpecialObj_XVel,Y

	LDA RandomN
	AND #$01
	BNE Birdo_Norm
	LDA #$28
	STA Objects_Timer, X


Birdo_Norm:
	JSR Object_HitTestRespond
	JSR Object_InteractWithWorld

ObjNorm_BirdoDraw:
	LDA <Objects_X, X
	LSR A
	LSR A
	AND #$01
	ORA Objects_Var1, X
	STA Objects_Frame, X
	JSR Object_Draw16x32Sprite
	RTS

ObjInit_PacBoo:
	LDA <Objects_X,X
	STA Objects_Var2, X
	LDA <Objects_XHi,X
	STA Objects_Var3, X

	LDA <Objects_Y,X
	STA Objects_Var4, X
	LDA <Objects_YHi,X
	STA Objects_Var5, X
	RTS

ObjNorm_PacBoo:
	LDA <Player_HaltGame
	BEQ ObjNorm_PacBoo1
	
	JMP Object_ShakeAndDraw	

ObjNorm_PacBoo1:
	LDA Objects_State, X
	CMP #OBJSTATE_KILLED
	BEQ ObjNorm_PacBoo3

	JSR Object_HitTestRespond
	LDA Player_StarInv
	BEQ ObjNorm_PacBoo2

	LDA #$01
	STA Objects_Frame, X

	JSR Chase
	LDA <Objects_XVel, X
	EOR #$FF
	STA <Objects_XVel, X
	LDA <Objects_YVel, X
	EOR #$FF
	STA <Objects_YVel, X

	JSR Object_ApplyXVel
	JSR Object_ApplyYVel

	LDA <Objects_XVel, X
	EOR #$FF
	STA <Objects_XVel, X

	LDA <Objects_YVel, X
	EOR #$FF
	STA <Objects_YVel, X

	LDA #SPR_PAL2
	STA Objects_SprAttr, X
	JMP Object_ShakeAndDraw

ObjNorm_PacBoo2:
	LDA #$00
	STA Objects_Frame, X

	LDA #SPR_PAL1
	STA Objects_SprAttr, X

	LDA RandomN, X
	AND #$03
	BNE ObjNorm_PacBoo21

	JSR Object_ApplyXVel
	JSR Object_ApplyYVel
	JMP Object_ShakeAndDraw

ObjNorm_PacBoo21:
	JSR Chase
	JMP Object_ShakeAndDraw
	
ObjNorm_PacBoo3:
	LDA Objects_Var1, X
	BNE ObjNorm_PacBoo4

	INC Objects_Var1, X

	LDA #$10
	STA Player_HaltTick

ObjNorm_PacBoo4:
	LDA #$02
	STA Objects_Frame, X

	LDA #SPR_PAL1
	STA Objects_SprAttr, X

	LDA <Player_X
	PHA
	LDA <Player_XHi
	PHA
	LDA <Player_Y
	PHA
	LDA <Player_YHi
	PHA

	LDA Objects_Var2, X
	STA <Player_X
	LDA Objects_Var3, X
	STA <Player_XHi

	LDA Objects_Var4, X
	STA <Player_Y
	LDA Objects_Var5, X
	STA <Player_YHi
	JSR Chase
	
	PLA
	STA <Player_YHi
	PLA
	STA <Player_Y
	PLA
	STA <Player_XHi
	PLA
	STA <Player_X

	JSR Object_GetAttrJustTile
	LDA Objects_LastProp, X
	CMP #TILE_PROP_ENEMY

	BNE ObjNorm_PacBoo5

	LDA #OBJSTATE_NORMAL
	STA Objects_State, X
	DEC Objects_Var1, X

ObjNorm_PacBoo5:
	JMP Object_ShakeAndDraw